mapper.select:
SELECT e.*, c.NAME AS className, u.NAME AS studentName, s.NAME AS courseName FROM examination_result e LEFT JOIN class c ON e.class_id = c.id LEFT JOIN USER u ON e.student_id = u.id LEFT JOIN COURSE s ON e.course_id = s.id
AND e.examination_time BETWEEN #{param.examinationTimeSt} AND #{param.examinationTimeEt}
AND e.score ${param.operateSymbol} #{param.score}
AND e.class_id = #{param.classId}
AND e.type in
#{item}
AND e.course_id = #{param.courseId}
AND u.name like concat('%', #{param.studentName}, '%')

SELECT CASE WHEN e.score = 100 THEN "100" WHEN e.score >= 90 THEN "90~99" WHEN e.score >= 80 THEN "80~89" WHEN e.score >= 70 THEN "70~79" WHEN e.score >= 60 THEN "60~69" ELSE "60以下" END NAME, count( 0 ) AS VALUE FROM examination_result e LEFT JOIN class c ON e.class_id = c.id LEFT JOIN USER u ON e.student_id = u.id LEFT JOIN COURSE s ON e.course_id = s.id
AND e.examination_time BETWEEN #{param.examinationTimeSt} AND #{param.examinationTimeEt}
AND e.score ${param.operateSymbol} #{param.score}
AND e.class_id = #{param.classId}
AND e.type in
#{item}
AND e.course_id = #{param.courseId}
AND u.name like concat('%', #{param.studentName}, '%')
group by NAME
