mapper.select:
SELECT w.*, c.NAME AS className, u.NAME AS studentName, s.NAME AS courseName FROM work_completion w LEFT JOIN class c ON w.class_id = c.id LEFT JOIN USER u ON w.student_id = u.id LEFT JOIN COURSE s ON w.course_id = s.id
AND w.statistics_time BETWEEN #{param.statisticsTimeSt} AND #{param.statisticsTimeEt}
AND w.COMPLETION_LEVEL = #{param.completionLevel}
AND w.class_id = #{param.classId}
AND w.course_id = #{param.courseId}
AND u.name like concat('%', #{param.studentName}, '%')

SELECT w.completion_level as id, count(0) as value FROM work_completion w LEFT JOIN class c ON w.class_id = c.id LEFT JOIN USER u ON w.student_id = u.id
AND w.statistics_time BETWEEN #{param.statisticsTimeSt} AND #{param.statisticsTimeEt}
AND w.COMPLETION_LEVEL = #{param.completionLevel}
AND w.class_id = #{param.classId}
AND w.course_id = #{param.courseId}
AND u.name like concat('%', #{param.studentName}, '%')
group by w.completion_level order by w.completion_level
